sum(
  (
    sum(container_memory_allocation_bytes{
      cluster="$cluster",
      job=~"$job",
      namespace=~"$namespace"
    })
    by (namespace, instance)
    * on(instance) group_left()
    (
      avg(
        node_ram_hourly_cost{
          cluster="$cluster",
          job=~"$job"
